pageContext.rootQuery:
{
    prismic {
        allPosts(sortBy: post_title_DESC) {
            edges {
                node {
                    post_title
                    post_category
                    post_preview_description
                    post_link {
                        __typename
                        ... on PRISMIC__ExternalLink {
                            url
                          }
                    }
                    post_hero_annotation
                    post_body
                    _meta {
                        uid
                    }
                }
            }
        }
    }
    site {
        siteMetadata {
            title
            description
            author
        }
    }
}
