pageContext.rootQuery:
{
    prismic {
        allProjects(sortBy: meta_firstPublicationDate_DESC) {
            edges {
                node {
                    project_title
                    project_preview_description
                    project_preview_thumbnail
                    project_category
                    project_link {
                        __typename 
                        ... on PRISMIC__ExternalLink{
                            url
                          }
                    }
                    project_post_date
                    _meta {
                        uid
                    }
                }
            }
        }
    }
    site {
        siteMetadata {
            title
            description
            author
        }
    }
}
