pageContext.rootQuery:
query PostQuery($uid: String) {
  prismic {
    allPosts(uid: $uid) {
      edges {
        node {
          post_title
          post_hero_annotation
          post_category
          post_body
          post_preview_description
          _meta {
            uid
          }
        }
      }
    }
  }
  site {
    siteMetadata {
      title
      description
      author
    }
  }
}
